                    "Separator": ":",
                    "Regex": "shenzhen"
                }
            },
            "EtlFilter": {
                "Filter": "{\"city\":[{\"contain\":\"shen\"}]}"
            },
            "Transform": {
                "OutputStructs": [
                    {
                        "Key": "city",
                        "Value": "$.city",
                        "ValueType": "JSONPATH"
                    },
                    {
                        "Key": "const",
                        "Value": "11",
                        "ValueType": "NUMBER"
                    },
                    {
                        "Key": "sdate",
                        "Value": "date",
                        "ValueType": "SYS_VARIABLE"
                    }
                ]
            }
        }
    ]
}

